// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;

interface ISnowGlobe {
    function name() external pure returns (string memory);
    function symbol() external pure returns (string memory);
    function decimals() external pure returns (uint8);
    function totalSupply() external view returns (uint);
    function balanceOf(address owner) external view returns (uint);
    function allowance(address owner, address spender) external view returns (uint);

    function approve(address spender, uint value) external returns (bool);
    function transfer(address to, uint value) external returns (bool);
    function transferFrom(address from, address to, uint value) external returns (bool);
    function increaseAllowance(address spender, uint256 addedValue) external returns (bool);
    function decreaseAllowance(address spender, uint256 subtractedValue) external returns (bool);


    function token() external view returns (address);
    function min() external view returns (uint256);
    function max() external view returns (uint256);

    function governance() external view returns (address);
    function timelock() external view returns (address);
    function controller() external view returns (address);

    function depositAll() external;
    function earn() external;
    function available() external view returns (uint256);
    function setController(address _controller) external;
    function getRatio() external view returns (uint256);
    function balance() external view returns (uint256);
    function deposit(uint256 _amount) external;
    function withdraw(uint256 _shares) external;
    function withdrawAll() external;
    function harvest(address reserve, uint256 amount) external;
}

contracts/IceQueenStrategyV2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "./SnowballERC20.sol";
import "./lib/SafeMath.sol";
import "./interfaces/IIceQueen.sol";
import "./interfaces/ISnowGlobe.sol";
import "./interfaces/IRouter.sol";
import "./interfaces/IPair.sol";
import "./interfaces/IERC20.sol";
import "./lib/Ownable.sol";

contract IceQueenStrategyV2 is SnowballERC20, Ownable {
  using SafeMath for uint;

  uint public totalDeposits;

  IRouter public router;
  ISnowGlobe public depositToken;
  IPair public lpToken;
  IERC20 private token0;
  IERC20 private token1;
  IERC20 public rewardToken;
  IIceQueen public stakingContract;

  uint public PID;
  uint public MIN_TOKENS_TO_REINVEST = 20000;
  uint public REINVEST_REWARD_BIPS = 500;
  uint public ADMIN_FEE_BIPS = 500;
  uint constant private BIPS_DIVISOR = 10000;
  bool public REQUIRE_REINVEST_BEFORE_DEPOSIT;
  uint public MIN_TOKENS_TO_REINVEST_BEFORE_DEPOSIT = 20;

  event Deposit(address account, uint amount);
  event Withdraw(address account, uint amount);
  event Reinvest(uint newTotalDeposits, uint newTotalSupply);
  event Recovered(address token, uint amount);
  event UpdateAdminFee(uint oldValue, uint newValue);
  event UpdateReinvestReward(uint oldValue, uint newValue);
  event UpdateMinTokensToReinvest(uint oldValue, uint newValue);
  event UpdateRequireReinvestBeforeDeposit(bool newValue);
  event UpdateMinTokensToReinvestBeforeDeposit(uint oldValue, uint newValue);

  constructor(
    address _depositToken,
    address _rewardToken, 
    address _stakingContract,
    address _router,
    uint _pid
  ) {
    depositToken = ISnowGlobe(_depositToken);
    rewardToken = IERC20(_rewardToken);
    stakingContract = IIceQueen(_stakingContract);
    router = IRouter(_router);

    address _lpToken = ISnowGlobe(_depositToken).token();
    lpToken = IPair(_lpToken);

    PID = _pid;

    address _token0 = IPair(_lpToken).token0();
    address _token1 = IPair(_lpToken).token1();
    token0 = IERC20(_token0);
    token1 = IERC20(_token1);

    name = string(
      abi.encodePacked(
        "Snowball: ",
        ISnowGlobe(_depositToken).symbol(), " (",
        lpToken.symbol(), " ",
        IERC20(_token0).symbol(), "-",
        IERC20(_token1).symbol(), ")"
      )
    );

    emit Reinvest(0, 0);
  }

  /**
    * @dev Throws if called by smart contract
    */
  modifier onlyEOA() {
      require(tx.origin == msg.sender, "onlyEOA");
      _;
  }

  /**
   * @notice Set approvals for tokens
   * @param tokensToApprove tokens to approve
   * @param approvalAmounts approval amounts
   * @param spenders address allowed to spend tokens
   */
  function tokenAllow(
      address[] memory tokensToApprove, 
      uint256[] memory approvalAmounts, 
      address[] memory spenders
  ) external onlyOwner {
    require(
      tokensToApprove.length == approvalAmounts.length
      && tokensToApprove.length == spenders.length
    , "not same length");
    for(uint i = 0; i < tokensToApprove.length; i++) {
      IERC20 token = IERC20(tokensToApprove[i]);
      uint allowance = token.allowance(address(this), spenders[i]);
      if (
        allowance != approvalAmounts[i] &&
        (allowance != uint256(-1) || approvalAmounts[i] == 0)
      ) {
        require(token.approve(spenders[i], approvalAmounts[i]), "approve failed");
      }
    }
  }

  /**
   * @notice Deposit LP tokens to receive Snowball tokens
   * @param amount Amount of LP tokens to deposit
   */
  function deposit(uint amount) external {
    require(totalDeposits >= totalSupply, "deposit failed");
    if (REQUIRE_REINVEST_BEFORE_DEPOSIT) {
      uint unclaimedRewards = checkReward();
      if (unclaimedRewards >= MIN_TOKENS_TO_REINVEST_BEFORE_DEPOSIT) {
        _reinvest(unclaimedRewards);
      }
    }
    require(depositToken.transferFrom(msg.sender, address(this), amount), "transferFrom failed");
    _stakeDepositTokens(amount);
    _mint(msg.sender, getSharesForLPTokens(amount));
    totalDeposits = totalDeposits.add(amount);
    emit Deposit(msg.sender, amount);
  }

  /**
   * @notice Withdraw deposit tokens by redeeming receipt tokens
   * @param amount Amount of receipt tokens to redeem
   */
  function withdraw(uint amount) external {
    uint depositTokenAmount = getLPTokensForShares(amount);
    if (depositTokenAmount > 0) {
      _withdrawDepositTokens(depositTokenAmount);
      require(depositToken.transfer(msg.sender, depositTokenAmount), "transfer failed");
      _burn(msg.sender, amount);
      totalDeposits = totalDeposits.sub(depositTokenAmount);
      emit Withdraw(msg.sender, depositTokenAmount);
    }
  }

  /**
   * @notice Calculate receipt tokens for a given amount of deposit tokens
   * @dev If contract is empty, use 1:1 ratio
   * @dev Could return zero shares for very low amounts of deposit tokens
   * @dev Note: misleading name (consider rename to "getReceiptTokensForDepositTokens")
   * @param amount deposit tokens
   * @return receipt tokens
   */
  function getSharesForLPTokens(uint amount) public view returns (uint) {
    if (totalSupply.mul(totalDeposits) == 0) {
      return amount;
    }
    return amount.mul(totalSupply).div(totalDeposits);
  }

  /**
   * @notice Calculate deposit tokens for a given amount of receipt tokens
   * @dev Note: misleading name (consider rename to "getDepositTokensForReceiptTokens")
   * @param amount receipt tokens
   * @return deposit tokens
   */
  function getLPTokensForShares(uint amount) public view returns (uint) {
    if (totalSupply.mul(totalDeposits) == 0) {
      return 0;
    }
    return amount.mul(totalDeposits).div(totalSupply);
  }

  /**
   * @notice Reward token balance that can be reinvested
   * @dev Staking rewards accurue to contract on each deposit/withdrawal
   * @return Unclaimed rewards, plus contract balance
   */
  function checkReward() public view returns (uint) {
    uint pendingReward = stakingContract.pendingSnowball(PID, address(this));
    uint contractBalance = rewardToken.balanceOf(address(this));
    return pendingReward.add(contractBalance);
  }

  /**
   * @notice Estimate reinvest reward
   * @return Estimated reward tokens earned for calling `reinvest()`
   */
  function estimateReinvestReward() external view returns (uint) {
    uint unclaimedRewards = checkReward();
    if (unclaimedRewards >= MIN_TOKENS_TO_REINVEST) {
      return unclaimedRewards.mul(REINVEST_REWARD_BIPS).div(BIPS_DIVISOR);
    }
    return 0;
  }

  /**
   * @notice Reinvest rewards from staking contract to LP tokens
   * @dev This external function requires minimum tokens to be met
   */
  function reinvest() external onlyEOA {
    uint unclaimedRewards = checkReward();
    require(unclaimedRewards >= MIN_TOKENS_TO_REINVEST, "MIN_TOKENS_TO_REINVEST");
    _reinvest(unclaimedRewards);
  }

  /**
   * @notice Reinvest rewards from staking contract to deposit tokens
   */
  function _reinvest(uint amount) internal {
    stakingContract.deposit(PID, 0);

    uint adminFee = amount.mul(ADMIN_FEE_BIPS).div(BIPS_DIVISOR);
    if (adminFee > 0) {
      require(rewardToken.transfer(owner(), adminFee), "admin fee transfer failed");
    }

    uint reinvestFee = amount.mul(REINVEST_REWARD_BIPS).div(BIPS_DIVISOR);
    if (reinvestFee > 0) {
      require(rewardToken.transfer(msg.sender, reinvestFee), "reinvest fee transfer failed");
    }

    uint lpTokenAmount = _convertRewardTokensToLpTokens(amount.sub(adminFee).sub(reinvestFee));
    uint depositTokenAmount = _convertLpTokensToDepositTokens(lpTokenAmount);
    _stakeDepositTokens(depositTokenAmount);
    totalDeposits = totalDeposits.add(depositTokenAmount);

    emit Reinvest(totalDeposits, totalSupply);
  }

  /**
   * @notice Converts intermediary LP tokens to deposit tokens
   * @dev Function `deposit(uint)` does not return an amount and is calculated
   * @dev Do NOT rely on output amount for non-standard token supply mechanisms (e.g. fee on transfer)
   * @return deposit tokens received
   */
  function _convertLpTokensToDepositTokens(uint amount) internal returns (uint) {
    require(amount > 0, "amount too low");
    uint256 _pool = depositToken.balance();
    uint256 _totalSupply = depositToken.totalSupply();
    uint256 shares = 0;
    if (_totalSupply == 0) {
        shares = amount;
    } else {
        shares = (amount.mul(_totalSupply)).div(_pool);
    }
    depositToken.deposit(amount);
    return shares;
  }

  /**
   * @notice Converts entire reward token balance to intermediary LP tokens
   * @dev Always converts through router; there are no price checks enabled
   * @return LP tokens received
   */
  function _convertRewardTokensToLpTokens(uint amount) internal returns (uint) {
    uint amountIn = amount.div(2);
    require(amountIn > 0, "amount too low");

    // swap to token0
    address[] memory path0 = new address[](3);
    path0[0] = address(rewardToken);
    path0[1] = 0xB31f66AA3C1e785363F0875A1B74E27b85FD66c7;
    path0[2] = address(token0);

    uint amountOutToken0 = amountIn;
    if (path0[0] != path0[path0.length - 1]) {
      uint[] memory amountsOutToken0 = router.getAmountsOut(amountIn, path0);
      amountOutToken0 = amountsOutToken0[amountsOutToken0.length - 1];
      router.swapExactTokensForTokens(amountIn, amountOutToken0, path0, address(this), block.timestamp);
    }

    // swap to token1
    address[] memory path1 = new address[](2);
    path1[0] = path0[0];
    path1[1] = address(token1);

    uint amountOutToken1 = amountIn;
    if (path1[0] != path1[path1.length - 1]) {
      uint[] memory amountsOutToken1 = router.getAmountsOut(amountIn, path1);
      amountOutToken1 = amountsOutToken1[amountsOutToken1.length - 1];
      router.swapExactTokensForTokens(amountIn, amountOutToken1, path1, address(this), block.timestamp);
    }

    (,,uint liquidity) = router.addLiquidity(
      path0[path0.length - 1], path1[path1.length - 1],
      amountOutToken0, amountOutToken1,
      0, 0,
      address(this),
      block.timestamp
    );

    return liquidity;
  }

  /**
   * @notice Stakes deposit tokens in Staking Contract
   * @param amount deposit tokens to stake
   */
  function _stakeDepositTokens(uint amount) internal {
    require(amount > 0, "amount too low");
    stakingContract.deposit(PID, amount);
  }

  /**
   * @notice Withdraws deposit tokens from Staking Contract
   * @dev Reward tokens are automatically collected
   * @dev Reward tokens are not automatically reinvested
   * @param amount deposit tokens to remove
   */
  function _withdrawDepositTokens(uint amount) internal {
    require(amount > 0, "amount too low");
    stakingContract.withdraw(PID, amount);
  }

  /**
   * @notice Allows exit from Staking Contract without additional logic
   * @dev Reward tokens are not automatically collected
   * @dev New deposits will be effectively disabled
   */
  function emergencyWithdraw() external onlyOwner {
    stakingContract.emergencyWithdraw(PID);
    totalDeposits = 0;
  }

  /**
   * @notice Update reinvest minimum threshold for external callers
   * @param newValue min threshold in wei
   */
  function updateMinTokensToReinvest(uint newValue) external onlyOwner {
    emit UpdateMinTokensToReinvest(MIN_TOKENS_TO_REINVEST, newValue);
    MIN_TOKENS_TO_REINVEST = newValue;
  }

  /**
   * @notice Update admin fee
   * @dev Total fees cannot be greater than BIPS_DIVISOR (100%)
   * @param newValue specified in BIPS
   */
  function updateAdminFee(uint newValue) external onlyOwner {
    require(newValue.add(REINVEST_REWARD_BIPS) <= BIPS_DIVISOR, "admin fee too high");
    emit UpdateAdminFee(ADMIN_FEE_BIPS, newValue);
    ADMIN_FEE_BIPS = newValue;
  }

  /**
   * @notice Update reinvest reward
   * @dev Total fees cannot be greater than BIPS_DIVISOR (100%)
   * @param newValue specified in BIPS
   */
  function updateReinvestReward(uint newValue) external onlyOwner {
    require(newValue.add(ADMIN_FEE_BIPS) <= BIPS_DIVISOR, "reinvest reward too high");
    emit UpdateReinvestReward(REINVEST_REWARD_BIPS, newValue);
    REINVEST_REWARD_BIPS = newValue;
  }

  /**
   * @notice Toggle requirement to reinvest before deposit
   */
  function updateRequireReinvestBeforeDeposit() external onlyOwner {
    REQUIRE_REINVEST_BEFORE_DEPOSIT = !REQUIRE_REINVEST_BEFORE_DEPOSIT;
    emit UpdateRequireReinvestBeforeDeposit(REQUIRE_REINVEST_BEFORE_DEPOSIT);
  }

  /**
   * @notice Update reinvest minimum threshold before a deposit
   * @param newValue min threshold in wei
   */
  function updateMinTokensToReinvestBeforeDeposit(uint newValue) external onlyOwner {
    emit UpdateMinTokensToReinvestBeforeDeposit(MIN_TOKENS_TO_REINVEST_BEFORE_DEPOSIT, newValue);
    MIN_TOKENS_TO_REINVEST_BEFORE_DEPOSIT = newValue;
  }

  /**
   * @notice Recover ERC20 from contract
   * @param tokenAddress token address
   * @param tokenAmount amount to recover
   */
  function recoverERC20(address tokenAddress, uint tokenAmount) external onlyOwner {
    require(tokenAmount > 0, 'amount too low');
    IERC20(tokenAddress).transfer(msg.sender, tokenAmount);
    emit Recovered(tokenAddress, tokenAmount);
  }

  /**
   * @notice Recover AVAX from contract
   * @param amount amount
   */
  function recoverAVAX(uint amount) external onlyOwner {
    require(amount > 0, 'amount too low');
    msg.sender.transfer(amount);
    emit Recovered(address(0), amount);
  }
}

contracts/IceQueenStrategy.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "./SnowballERC20.sol";
import "./lib/SafeMath.sol";
import "./interfaces/IIceQueen.sol";
import "./interfaces/IRouter.sol";
import "./interfaces/IPair.sol";
import "./interfaces/IERC20.sol";
import "./lib/Ownable.sol";

contract IceQueenStrategy is SnowballERC20, Ownable {
  using SafeMath for uint;

  uint public totalDeposits;

  IRouter public router;
  IPair public lpToken;
  IERC20 private token0;
  IERC20 private token1;
  IERC20 public rewardToken;
  IIceQueen public stakingContract;

  uint public PID;
  uint public MIN_TOKENS_TO_REINVEST = 20;
  uint public REINVEST_REWARD_BIPS = 500;
  uint public ADMIN_FEE_BIPS = 500;
  uint constant private BIPS_DIVISOR = 10000;

  event Deposit(address account, uint amount);
  event Withdraw(address account, uint amount);
  event Reinvest(uint newTotalDeposits, uint newTotalSupply);
  event Recovered(address token, uint amount);
  event UpdateAdminFee(uint oldValue, uint newValue);
  event UpdateReinvestReward(uint oldValue, uint newValue);
  event UpdateMinTokensToReinvest(uint oldValue, uint newValue);

  constructor(
    address _lpToken, 
    address _rewardToken, 
    address _stakingContract,
    address _router,
    uint _pid
  ) {
    lpToken = IPair(_lpToken);
    rewardToken = IERC20(_rewardToken);
    stakingContract = IIceQueen(_stakingContract);
    router = IRouter(_router);

    PID = _pid;

    address _token0 = IPair(_lpToken).token0();
    address _token1 = IPair(_lpToken).token1();
    token0 = IERC20(_token0);
    token1 = IERC20(_token1);

    IERC20(_rewardToken).approve(_router, uint(-1));
    IERC20(_token0).approve(_router, uint(-1));
    IERC20(_token1).approve(_router, uint(-1));
    IPair(_lpToken).approve(_stakingContract, uint(-1));

    name = string(
      abi.encodePacked(
        "Snowball: ",
        lpToken.symbol(), " ",
        IERC20(_token0).symbol(), "-",
        IERC20(_token1).symbol()
      )
    );
  }

  /**
    * @dev Throws if called by smart contract
    */
  modifier onlyEOA() {
      require(tx.origin == msg.sender, "onlyEOA");
      _;
  }

  /**
   * @notice Deposit LP tokens to receive Snowball tokens
   * @param amount Amount of LP tokens to deposit
   */
  function deposit(uint amount) external {
    _deposit(amount);
  }

  /**
   * @notice Deposit LP tokens to receive Snowball tokens
   * @param amount Amount of LP tokens to deposit
   * @param deadline The time at which to expire the signature
   * @param v The recovery byte of the signature
   * @param r Half of the ECDSA signature pair
   * @param s Half of the ECDSA signature pair
   */
  function depositWithPermit(uint amount, uint deadline, uint8 v, bytes32 r, bytes32 s) external {
    lpToken.permit(msg.sender, address(this), amount, deadline, v, r, s);
    _deposit(amount);
  }

  function _deposit(uint amount) internal {
    require(totalDeposits >= totalSupply, "deposit failed");
    require(lpToken.transferFrom(msg.sender, address(this), amount), "transferFrom failed");
    _stakeLpTokens(amount);
    _mint(msg.sender, getSharesForLPTokens(amount));
    totalDeposits = totalDeposits.add(amount);
    emit Deposit(msg.sender, amount);
  }

  /**
   * @notice Withdraw LP tokens by redeeming Snowball tokens
   * @param amount Amount of Snowball tokens to redeem
   */
  function withdraw(uint amount) external {
    uint lpTokenAmount = getLPTokensForShares(amount);
    if (lpTokenAmount > 0) {
      _withdrawLpTokens(lpTokenAmount);
      require(lpToken.transfer(msg.sender, lpTokenAmount), "transfer failed");
      _burn(msg.sender, amount);
      totalDeposits = totalDeposits.sub(lpTokenAmount);
      emit Withdraw(msg.sender, lpTokenAmount);
    }
  }

  /**
   * @notice Calculate Snowball tokens for a given amount of LP tokens
   * @dev If contract is empty, use 1:1 ratio
   * @dev Could return zero shares for very low amounts of LP tokens
   * @param amount LP tokens
   * @return Snowball tokens
   */
  function getSharesForLPTokens(uint amount) public view returns (uint) {
    if (totalSupply.mul(totalDeposits) == 0) {
      return amount;
    }
    return amount.mul(totalSupply).div(totalDeposits);
  }

  /**
   * @notice Calculate LP tokens for a given amount of Snowball tokens
   * @param amount Snowball tokens
   * @return LP tokens
   */
  function getLPTokensForShares(uint amount) public view returns (uint) {
    if (totalSupply.mul(totalDeposits) == 0) {
      return 0;
    }
    return amount.mul(totalDeposits).div(totalSupply);
  }

  /**
   * @notice Reward token balance that can be reinvested
   * @dev Staking rewards accurue to contract on each deposit/withdrawal
   * @return Unclaimed rewards, plus contract balance
   */
  function checkReward() public view returns (uint) {
    uint pendingReward = stakingContract.pendingSnowball(PID, address(this));
    uint contractBalance = rewardToken.balanceOf(address(this));
    return pendingReward.add(contractBalance);
  }

  /**
   * @notice Estimate reinvest reward for caller
   * @return Estimated rewards tokens earned for calling `reinvest()`
   */
  function estimateReinvestReward() external view returns (uint) {
    uint unclaimedRewards = checkReward();
    if (unclaimedRewards >= MIN_TOKENS_TO_REINVEST) {
      return unclaimedRewards.mul(REINVEST_REWARD_BIPS).div(BIPS_DIVISOR);
    }
    return 0;
  }

  /**
   * @notice Reinvest rewards from staking contract to LP tokens
   */
  function reinvest() external onlyEOA {
    uint unclaimedRewards = checkReward();
    require(unclaimedRewards >= MIN_TOKENS_TO_REINVEST, "MIN_TOKENS_TO_REINVEST");
    stakingContract.deposit(PID, 0);

    uint adminFee = unclaimedRewards.mul(ADMIN_FEE_BIPS).div(BIPS_DIVISOR);
    if (adminFee > 0) {
      require(rewardToken.transfer(owner(), adminFee), "admin fee transfer failed");
    }

    uint reinvestFee = unclaimedRewards.mul(REINVEST_REWARD_BIPS).div(BIPS_DIVISOR);
    if (reinvestFee > 0) {
      require(rewardToken.transfer(msg.sender, reinvestFee), "reinvest fee transfer failed");
    }

    uint lpTokenAmount = _convertRewardTokensToLpTokens(unclaimedRewards.sub(adminFee).sub(reinvestFee));
    _stakeLpTokens(lpTokenAmount);
    totalDeposits = totalDeposits.add(lpTokenAmount);

    emit Reinvest(totalDeposits, totalSupply);
  }

  /**
   * @notice Converts entire reward token balance to LP tokens
   * @dev Always converts through router; there are no price checks enabled
   * @return LP tokens received
   */
  function _convertRewardTokensToLpTokens(uint amount) internal returns (uint) {
    uint amountIn = amount.div(2);
    require(amountIn > 0, "amount too low");

    // swap to token0
    address[] memory path0 = new address[](2);
    path0[0] = address(rewardToken);
    path0[1] = address(token0);

    uint amountOutToken0 = amountIn;
    if (path0[0] != path0[1]) {
      uint[] memory amountsOutToken0 = router.getAmountsOut(amountIn, path0);
      amountOutToken0 = amountsOutToken0[amountsOutToken0.length - 1];
      router.swapExactTokensForTokens(amountIn, amountOutToken0, path0, address(this), block.timestamp);
    }

    // swap to token1
    address[] memory path1 = new address[](2);
    path1[0] = path0[0];
    path1[1] = address(token1);

    uint amountOutToken1 = amountIn;
    if (path1[0] != path1[1]) {
      uint[] memory amountsOutToken1 = router.getAmountsOut(amountIn, path1);
      amountOutToken1 = amountsOutToken1[amountsOutToken1.length - 1];
      router.swapExactTokensForTokens(amountIn, amountOutToken1, path1, address(this), block.timestamp);
    }

    (,,uint liquidity) = router.addLiquidity(
      path0[1], path1[1],
      amountOutToken0, amountOutToken1,
      0, 0,
      address(this),
      block.timestamp
    );

    return liquidity;
  }

  /**
   * @notice Stakes LP tokens in Staking Contract
   * @param amount LP tokens to stake
   */
  function _stakeLpTokens(uint amount) internal {
    require(amount > 0, "amount too low");
    stakingContract.deposit(PID, amount);
  }

  /**
   * @notice Withdraws LP tokens from Staking Contract
   * @dev Rewards are not automatically collected from the Staking Contract
   * @param amount LP tokens to remove;
   */
  function _withdrawLpTokens(uint amount) internal {
    require(amount > 0, "amount too low");
    stakingContract.withdraw(PID, amount);
  }

  /**
   * @notice Allows exit from Staking Contract without additional logic
   */
  function emergencyWithdraw() external onlyOwner {
    stakingContract.emergencyWithdraw(PID);
    totalDeposits = 0;
  }

  /**
   * @notice Update reinvest minimum threshold
   * @param newValue min threshold in wei
   */
  function updateMinTokensToReinvest(uint newValue) external onlyOwner {
    emit UpdateMinTokensToReinvest(MIN_TOKENS_TO_REINVEST, newValue);
    MIN_TOKENS_TO_REINVEST = newValue;
  }

  /**
   * @notice Update admin fee
   * @dev Total fees cannot be greater than BIPS_DIVISOR (100%)
   * @param newValue specified in BIPS
   */
  function updateAdminFee(uint newValue) external onlyOwner {
    require(newValue.add(REINVEST_REWARD_BIPS) <= BIPS_DIVISOR, "admin fee too high");
    emit UpdateAdminFee(ADMIN_FEE_BIPS, newValue);
    ADMIN_FEE_BIPS = newValue;
  }

  /**
   * @notice Update reinvest reward
   * @dev Total fees cannot be greater than BIPS_DIVISOR (100%)
   * @param newValue specified in BIPS
   */
  function updateReinvestReward(uint newValue) external onlyOwner {
    require(newValue.add(ADMIN_FEE_BIPS) <= BIPS_DIVISOR, "reinvest reward too high");
    emit UpdateReinvestReward(REINVEST_REWARD_BIPS, newValue);
    REINVEST_REWARD_BIPS = newValue;
  }

  /**
   * @notice Recover ERC20 from contract
   * @param tokenAddress token address
   * @param tokenAmount amount to recover
   */
  function recoverERC20(address tokenAddress, uint tokenAmount) external onlyOwner {
    require(tokenAmount > 0, 'amount too low');
    IERC20(tokenAddress).transfer(msg.sender, tokenAmount);
    emit Recovered(tokenAddress, tokenAmount);
  }

  /**
   * @notice Recover AVAX from contract
   * @param amount amount
   */
  function recoverAVAX(uint amount) external onlyOwner {
    require(amount > 0, 'amount too low');
    msg.sender.transfer(amount);
    emit Recovered(address(0), amount);
  }
}

contracts/DexStrategyV2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "./SnowballERC20.sol";
import "./lib/SafeMath.sol";
import "./interfaces/IStakingRewards.sol";
import "./interfaces/IRouter.sol";
import "./interfaces/IPair.sol";
import "./interfaces/IERC20.sol";
import "./lib/Ownable.sol";

contract DexStrategyV2 is SnowballERC20, Ownable {
  using SafeMath for uint;

  uint public totalDeposits;

  IRouter public router;
  IPair public depositToken;
  IERC20 private token0;
  IERC20 private token1;
  IERC20 public rewardToken;
  IStakingRewards public stakingContract;

  uint public MIN_TOKENS_TO_REINVEST = 20000;
  uint public REINVEST_REWARD_BIPS = 500;
  uint public ADMIN_FEE_BIPS = 500;
  uint constant private BIPS_DIVISOR = 10000;
  bool public REQUIRE_REINVEST_BEFORE_DEPOSIT;
  uint public MIN_TOKENS_TO_REINVEST_BEFORE_DEPOSIT = 20;

  event Deposit(address indexed account, uint amount);
  event Withdraw(address indexed account, uint amount);
  event Reinvest(uint newTotalDeposits, uint newTotalSupply);
  event Recovered(address token, uint amount);
  event UpdateAdminFee(uint oldValue, uint newValue);
  event UpdateReinvestReward(uint oldValue, uint newValue);
  event UpdateMinTokensToReinvest(uint oldValue, uint newValue);
  event UpdateRequireReinvestBeforeDeposit(bool newValue);
  event UpdateMinTokensToReinvestBeforeDeposit(uint oldValue, uint newValue);

  constructor(
    address _depositToken,
    address _rewardToken, 
    address _stakingContract, 
    address _router
  ) {
    depositToken = IPair(_depositToken);
    rewardToken = IERC20(_rewardToken);
    stakingContract = IStakingRewards(_stakingContract);
    router = IRouter(_router);

    address _token0 = IPair(depositToken).token0();
    address _token1 = IPair(depositToken).token1();
    token0 = IERC20(_token0);
    token1 = IERC20(_token1);

    name = string(
      abi.encodePacked(
        "Snowball: ",
        depositToken.symbol(), " ",
        IERC20(_token0).symbol(), "-",
        IERC20(_token1).symbol()
      )
    );

    emit Reinvest(0, 0);
  }

  /**
    * @dev Throws if called by smart contract
    */
  modifier onlyEOA() {
      require(tx.origin == msg.sender, "onlyEOA");
      _;
  }

  /**
   * @notice Set approvals for tokens
   * @param tokensToApprove tokens to approve
   * @param approvalAmounts approval amounts
   * @param spenders address allowed to spend tokens
   */
  function tokenAllow(
      address[] memory tokensToApprove, 
      uint256[] memory approvalAmounts, 
      address[] memory spenders
  ) external onlyOwner {
    require(
      tokensToApprove.length == approvalAmounts.length
      && tokensToApprove.length == spenders.length
    , "not same length");
    for(uint i = 0; i < tokensToApprove.length; i++) {
      IERC20 token = IERC20(tokensToApprove[i]);
      uint allowance = token.allowance(address(this), spenders[i]);
      if (
        allowance != approvalAmounts[i] &&
        (allowance != uint256(-1) || approvalAmounts[i] == 0)
      ) {
        require(token.approve(spenders[i], approvalAmounts[i]), "approve failed");
      }
    }
  }


  /**
   * @notice Deposit tokens to receive receipt tokens
   * @param amount Amount of tokens to deposit
   */
  function deposit(uint amount) external {
    _deposit(amount);
  }

  /**
   * @notice Deposit using Permit
   * @param amount Amount of tokens to deposit
   * @param deadline The time at which to expire the signature
   * @param v The recovery byte of the signature
   * @param r Half of the ECDSA signature pair
   * @param s Half of the ECDSA signature pair
   */
  function depositWithPermit(uint amount, uint deadline, uint8 v, bytes32 r, bytes32 s) external {
    depositToken.permit(msg.sender, address(this), amount, deadline, v, r, s);
    _deposit(amount);
  }

  function _deposit(uint amount) internal {
    require(totalDeposits >= totalSupply, "deposit failed");
    if (REQUIRE_REINVEST_BEFORE_DEPOSIT) {
      uint unclaimedRewards = checkReward();
      if (unclaimedRewards >= MIN_TOKENS_TO_REINVEST_BEFORE_DEPOSIT) {
        _reinvest(unclaimedRewards);
      }
    }
    require(depositToken.transferFrom(msg.sender, address(this), amount), "transferFrom failed");
    _stakeDepositTokens(amount);
    _mint(msg.sender, getSharesForDepositTokens(amount));
    totalDeposits = totalDeposits.add(amount);
    emit Deposit(msg.sender, amount);
  }

  /**
   * @notice Withdraw LP tokens by redeeming receipt tokens
   * @param amount Amount of receipt tokens to redeem
   */
  function withdraw(uint amount) external {
    uint depositTokenAmount = getDepositTokensForShares(amount);
    if (depositTokenAmount > 0) {
      _withdrawDepositTokens(depositTokenAmount);
      require(depositToken.transfer(msg.sender, depositTokenAmount), "transfer failed");
      _burn(msg.sender, amount);
      totalDeposits = totalDeposits.sub(depositTokenAmount);
      emit Withdraw(msg.sender, depositTokenAmount);
    }
  }

  /**
   * @notice Calculate receipt tokens for a given amount of deposit tokens
   * @dev If contract is empty, use 1:1 ratio
   * @dev Could return zero shares for very low amounts of deposit tokens
   * @param amount deposit tokens
   * @return receipt tokens
   */
  function getSharesForDepositTokens(uint amount) public view returns (uint) {
    if (totalSupply.mul(totalDeposits) == 0) {
      return amount;
    }
    return amount.mul(totalSupply).div(totalDeposits);
  }

  /**
   * @notice Calculate deposit tokens for a given amount of receipt tokens
   * @param amount receipt tokens
   * @return deposit tokens
   */
  function getDepositTokensForShares(uint amount) public view returns (uint) {
    if (totalSupply.mul(totalDeposits) == 0) {
      return 0;
    }
    return amount.mul(totalDeposits).div(totalSupply);
  }

  /**
   * @notice Unclaimed rewards from staking contract
   * @return Unclaimed rewards from staking contract
   */
  function checkReward() public view returns (uint) {
    return stakingContract.earned(address(this));
  }

  /**
   * @notice Estimate reinvest reward for caller
   * @return Estimated rewards tokens earned for calling `reinvest()`
   */
  function estimateReinvestReward() external view returns (uint) {
    uint unclaimedRewards = checkReward();
    if (unclaimedRewards >= MIN_TOKENS_TO_REINVEST) {
      return unclaimedRewards.mul(REINVEST_REWARD_BIPS).div(BIPS_DIVISOR);
    }
    return 0;
  }

  /**
   * @notice Reinvest rewards from staking contract to deposit tokens
   * @dev This external function requires minimum tokens to be met
   */
  function reinvest() external onlyEOA {
    uint unclaimedRewards = checkReward();
    require(unclaimedRewards >= MIN_TOKENS_TO_REINVEST, "MIN_TOKENS_TO_REINVEST");
    _reinvest(unclaimedRewards);
  }

  /**
   * @notice Reinvest rewards from staking contract to deposit tokens
   * @dev This internal function does not require mininmum tokens to be met
   */
  function _reinvest(uint amount) internal {
    stakingContract.getReward();

    uint adminFee = amount.mul(ADMIN_FEE_BIPS).div(BIPS_DIVISOR);
    if (adminFee > 0) {
      require(rewardToken.transfer(owner(), adminFee), "admin fee transfer failed");
    }

    uint reinvestFee = amount.mul(REINVEST_REWARD_BIPS).div(BIPS_DIVISOR);
    if (reinvestFee > 0) {
      require(rewardToken.transfer(msg.sender, reinvestFee), "reinvest fee transfer failed");
    }

    uint lpTokenAmount = _convertRewardTokensToDepositTokens(amount.sub(adminFee).sub(reinvestFee));
    _stakeDepositTokens(lpTokenAmount);
    totalDeposits = totalDeposits.add(lpTokenAmount);

    emit Reinvest(totalDeposits, totalSupply);
  }

  /**
   * @notice Converts entire reward token balance to deposit tokens
   * @dev Always converts through router; there are no price checks enabled
   * @return deposit tokens received
   */
  function _convertRewardTokensToDepositTokens(uint amount) internal returns (uint) {
    uint amountIn = amount.div(2);
    require(amountIn > 0, "amount too low");

    // swap to token0
    address[] memory path0 = new address[](2);
    path0[0] = address(rewardToken);
    path0[1] = address(token0);

    uint amountOutToken0 = amountIn;
    if (path0[0] != path0[1]) {
      uint[] memory amountsOutToken0 = router.getAmountsOut(amountIn, path0);
      amountOutToken0 = amountsOutToken0[amountsOutToken0.length - 1];
      router.swapExactTokensForTokens(amountIn, amountOutToken0, path0, address(this), block.timestamp);
    }

    // swap to token1
    address[] memory path1 = new address[](2);
    path1[0] = path0[0];
    path1[1] = address(token1);

    uint amountOutToken1 = amountIn;
    if (path1[0] != path1[1]) {
      uint[] memory amountsOutToken1 = router.getAmountsOut(amountIn, path1);
      amountOutToken1 = amountsOutToken1[amountsOutToken1.length - 1];
      router.swapExactTokensForTokens(amountIn, amountOutToken1, path1, address(this), block.timestamp);
    }

    (,,uint liquidity) = router.addLiquidity(
      path0[1], path1[1],
      amountOutToken0, amountOutToken1,
      0, 0,
      address(this),
      block.timestamp
    );

    return liquidity;
  }

  /**
   * @notice Stakes deposit tokens in Staking Contract
   * @param amount deposit tokens to stake
   */
  function _stakeDepositTokens(uint amount) internal {
    require(amount > 0, "amount too low");
    stakingContract.stake(amount);
  }

  /**
   * @notice Withdraws deposit tokens from Staking Contract
   * @dev Reward tokens are automatically collected
   * @dev Reward tokens are not automatically reinvested
   * @param amount deposit tokens to remove
   */
  function _withdrawDepositTokens(uint amount) internal {
    require(amount > 0, "amount too low");
    stakingContract.withdraw(amount);
  }

  /**
   * @notice Allows exit from Staking Contract without additional logic
   * @dev Reward tokens are not automatically collected
   * @dev New deposits will be effectively disabled
   */
  function emergencyWithdraw() external onlyOwner {
    stakingContract.exit();
    totalDeposits = 0;
  }

  /**
   * @notice Update reinvest minimum threshold for external callers
   * @param newValue min threshold in wei
   */
  function updateMinTokensToReinvest(uint newValue) external onlyOwner {
    emit UpdateMinTokensToReinvest(MIN_TOKENS_TO_REINVEST, newValue);
    MIN_TOKENS_TO_REINVEST = newValue;
  }

  /**
   * @notice Update admin fee
   * @dev Total fees cannot be greater than BIPS_DIVISOR (100%)
   * @param newValue specified in BIPS
   */
  function updateAdminFee(uint newValue) external onlyOwner {
    require(newValue.add(REINVEST_REWARD_BIPS) <= BIPS_DIVISOR, "admin fee too high");
    emit UpdateAdminFee(ADMIN_FEE_BIPS, newValue);
    ADMIN_FEE_BIPS = newValue;
  }

  /**
   * @notice Update reinvest reward
   * @dev Total fees cannot be greater than BIPS_DIVISOR (100%)
   * @param newValue specified in BIPS
   */
  function updateReinvestReward(uint newValue) external onlyOwner {
    require(newValue.add(ADMIN_FEE_BIPS) <= BIPS_DIVISOR, "reinvest reward too high");
    emit UpdateReinvestReward(REINVEST_REWARD_BIPS, newValue);
    REINVEST_REWARD_BIPS = newValue;
  }

  /**
   * @notice Toggle requirement to reinvest before deposit
   */
  function updateRequireReinvestBeforeDeposit() external onlyOwner {
    REQUIRE_REINVEST_BEFORE_DEPOSIT = !REQUIRE_REINVEST_BEFORE_DEPOSIT;
    emit UpdateRequireReinvestBeforeDeposit(REQUIRE_REINVEST_BEFORE_DEPOSIT);
  }

  /**
   * @notice Update reinvest minimum threshold before a deposit
   * @param newValue min threshold in wei
   */
  function updateMinTokensToReinvestBeforeDeposit(uint newValue) external onlyOwner {
    emit UpdateMinTokensToReinvestBeforeDeposit(MIN_TOKENS_TO_REINVEST_BEFORE_DEPOSIT, newValue);
    MIN_TOKENS_TO_REINVEST_BEFORE_DEPOSIT = newValue;
  }

  /**
   * @notice Recover ERC20 from contract
   * @param tokenAddress token address
   * @param tokenAmount amount to recover
   */
  function recoverERC20(address tokenAddress, uint tokenAmount) external onlyOwner {
    require(tokenAmount > 0, 'amount too low');
    IERC20(tokenAddress).transfer(msg.sender, tokenAmount);
    emit Recovered(tokenAddress, tokenAmount);
  }

  /**
   * @notice Recover AVAX from contract
   * @param amount amount
   */
  function recoverAVAX(uint amount) external onlyOwner {
    require(amount > 0, 'amount too low');
    msg.sender.transfer(amount);
    emit Recovered(address(0), amount);
  }
}

contracts/DexStrategy.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "./SnowballERC20.sol";
import "./lib/SafeMath.sol";
import "./interfaces/IStakingRewards.sol";
import "./interfaces/IRouter.sol";
import "./interfaces/IPair.sol";
import "./interfaces/IERC20.sol";
import "./lib/Ownable.sol";

contract DexStrategy is SnowballERC20, Ownable {
  using SafeMath for uint;

  uint public totalDeposits;

  IRouter public router;
  IPair public lpToken;
  IERC20 private token0;
  IERC20 private token1;
  IERC20 public rewardToken;
  IStakingRewards public stakingContract;

  uint public MIN_TOKENS_TO_REINVEST = 20;
  uint public REINVEST_REWARD_BIPS = 500;
  uint public ADMIN_FEE_BIPS = 500;
  uint constant private BIPS_DIVISOR = 10000;

  event Deposit(address account, uint amount);
  event Withdraw(address account, uint amount);
  event Reinvest(uint newTotalDeposits, uint newTotalSupply);
  event Recovered(address token, uint amount);
  event UpdateAdminFee(uint oldValue, uint newValue);
  event UpdateReinvestReward(uint oldValue, uint newValue);
  event UpdateMinTokensToReinvest(uint oldValue, uint newValue);

  constructor(address _lpToken, address _rewardToken, address _stakingContract, address _router) {
    lpToken = IPair(_lpToken);
    rewardToken = IERC20(_rewardToken);
    stakingContract = IStakingRewards(_stakingContract);
    router = IRouter(_router);

    address _token0 = IPair(_lpToken).token0();
    address _token1 = IPair(_lpToken).token1();
    token0 = IERC20(_token0);
    token1 = IERC20(_token1);

    IERC20(_rewardToken).approve(_router, uint(-1));
    IERC20(_token0).approve(_router, uint(-1));
    IERC20(_token1).approve(_router, uint(-1));
    IPair(_lpToken).approve(_stakingContract, uint(-1));

    name = string(
      abi.encodePacked(
        "Snowball: ",
        lpToken.symbol(), " ",
        IERC20(_token0).symbol(), "-",
        IERC20(_token1).symbol()
      )
    );
  }

  /**
    * @dev Throws if called by smart contract
    */
  modifier onlyEOA() {
      require(tx.origin == msg.sender, "onlyEOA");
      _;
  }

  /**
   * @notice Deposit LP tokens to receive Snowball tokens
   * @param amount Amount of LP tokens to deposit
   */
  function deposit(uint amount) external {
    _deposit(amount);
  }

  /**
   * @notice Deposit LP tokens to receive Snowball tokens
   * @param amount Amount of LP tokens to deposit
   * @param deadline The time at which to expire the signature
   * @param v The recovery byte of the signature
   * @param r Half of the ECDSA signature pair
   * @param s Half of the ECDSA signature pair
   */
  function depositWithPermit(uint amount, uint deadline, uint8 v, bytes32 r, bytes32 s) external {
    lpToken.permit(msg.sender, address(this), amount, deadline, v, r, s);
    _deposit(amount);
  }

  function _deposit(uint amount) internal {
    require(totalDeposits >= totalSupply, "deposit failed");
    require(lpToken.transferFrom(msg.sender, address(this), amount), "transferFrom failed");
    _stakeLpTokens(amount);
    _mint(msg.sender, getSharesForLPTokens(amount));
    totalDeposits = totalDeposits.add(amount);
    emit Deposit(msg.sender, amount);
  }

  /**
   * @notice Withdraw LP tokens by redeeming Snowball tokens
   * @param amount Amount of Snowball tokens to redeem
   */
  function withdraw(uint amount) external {
    uint lpTokenAmount = getLPTokensForShares(amount);
    if (lpTokenAmount > 0) {
      _withdrawLpTokens(lpTokenAmount);
      require(lpToken.transfer(msg.sender, lpTokenAmount), "transfer failed");
      _burn(msg.sender, amount);
      totalDeposits = totalDeposits.sub(lpTokenAmount);
      emit Withdraw(msg.sender, lpTokenAmount);
    }
  }

  /**
   * @notice Calculate Snowball tokens for a given amount of LP tokens
   * @dev If contract is empty, use 1:1 ratio
   * @dev Could return zero shares for very low amounts of LP tokens
   * @param amount LP tokens
   * @return Snowball tokens
   */
  function getSharesForLPTokens(uint amount) public view returns (uint) {
    if (totalSupply.mul(totalDeposits) == 0) {
      return amount;
    }
    return amount.mul(totalSupply).div(totalDeposits);
  }

  /**
   * @notice Calculate LP tokens for a given amount of Snowball tokens
   * @param amount Snowball tokens
   * @return LP tokens
   */
  function getLPTokensForShares(uint amount) public view returns (uint) {
    if (totalSupply.mul(totalDeposits) == 0) {
      return 0;
    }
    return amount.mul(totalDeposits).div(totalSupply);
  }

  /**
   * @notice Unclaimed rewards from staking contract
   * @return Unclaimed rewards from staking contract
   */
  function checkReward() public view returns (uint) {
    return stakingContract.earned(address(this));
  }

  /**
   * @notice Estimate reinvest reward for caller
   * @return Estimated rewards tokens earned for calling `reinvest()`
   */
  function estimateReinvestReward() external view returns (uint) {
    uint unclaimedRewards = checkReward();
    if (unclaimedRewards >= MIN_TOKENS_TO_REINVEST) {
      return unclaimedRewards.mul(REINVEST_REWARD_BIPS).div(BIPS_DIVISOR);
    }
    return 0;
  }

  /**
   * @notice Reinvest rewards from staking contract to LP tokens
   */
  function reinvest() external onlyEOA {
    uint unclaimedRewards = checkReward();
    require(unclaimedRewards >= MIN_TOKENS_TO_REINVEST, "MIN_TOKENS_TO_REINVEST");
    stakingContract.getReward();

    uint adminFee = unclaimedRewards.mul(ADMIN_FEE_BIPS).div(BIPS_DIVISOR);
    if (adminFee > 0) {
      require(rewardToken.transfer(owner(), adminFee), "admin fee transfer failed");
    }

    uint reinvestFee = unclaimedRewards.mul(REINVEST_REWARD_BIPS).div(BIPS_DIVISOR);
    if (reinvestFee > 0) {
      require(rewardToken.transfer(msg.sender, reinvestFee), "reinvest fee transfer failed");
    }

    uint lpTokenAmount = _convertRewardTokensToLpTokens(unclaimedRewards.sub(adminFee).sub(reinvestFee));
    _stakeLpTokens(lpTokenAmount);
    totalDeposits = totalDeposits.add(lpTokenAmount);

    emit Reinvest(totalDeposits, totalSupply);
  }

  /**
   * @notice Converts entire reward token balance to LP tokens
   * @dev Always converts through router; there are no price checks enabled
   * @return LP tokens received
   */
  function _convertRewardTokensToLpTokens(uint amount) internal returns (uint) {
    uint amountIn = amount.div(2);
    require(amountIn > 0, "amount too low");

    // swap to token0
    address[] memory path0 = new address[](2);
    path0[0] = address(rewardToken);
    path0[1] = address(token0);

    uint amountOutToken0 = amountIn;
    if (path0[0] != path0[1]) {
      uint[] memory amountsOutToken0 = router.getAmountsOut(amountIn, path0);
      amountOutToken0 = amountsOutToken0[amountsOutToken0.length - 1];
      router.swapExactTokensForTokens(amountIn, amountOutToken0, path0, address(this), block.timestamp);
    }

    // swap to token1
    address[] memory path1 = new address[](2);
    path1[0] = path0[0];
    path1[1] = address(token1);

    uint amountOutToken1 = amountIn;
    if (path1[0] != path1[1]) {
      uint[] memory amountsOutToken1 = router.getAmountsOut(amountIn, path1);
      amountOutToken1 = amountsOutToken1[amountsOutToken1.length - 1];
      router.swapExactTokensForTokens(amountIn, amountOutToken1, path1, address(this), block.timestamp);
    }

    (,,uint liquidity) = router.addLiquidity(
      path0[1], path1[1],
      amountOutToken0, amountOutToken1,
      0, 0,
      address(this),
      block.timestamp
    );

    return liquidity;
  }

  /**
   * @notice Stakes LP tokens in Staking Contract
   * @param amount LP tokens to stake
   */
  function _stakeLpTokens(uint amount) internal {
    require(amount > 0, "amount too low");
    stakingContract.stake(amount);
  }

  /**
   * @notice Withdraws LP tokens from Staking Contract
   * @dev Rewards are not automatically collected from the Staking Contract
   * @param amount LP tokens to remove;
   */
  function _withdrawLpTokens(uint amount) internal {
    require(amount > 0, "amount too low");
    stakingContract.withdraw(amount);
  }

  /**
   * @notice Allows exit from Staking Contract without additional logic
   */
  function emergencyWithdraw() external onlyOwner {
    stakingContract.exit();
    totalDeposits = 0;
  }

  /**
   * @notice Update reinvest minimum threshold
   * @param newValue min threshold in wei
   */
  function updateMinTokensToReinvest(uint newValue) external onlyOwner {
    emit UpdateMinTokensToReinvest(MIN_TOKENS_TO_REINVEST, newValue);
    MIN_TOKENS_TO_REINVEST = newValue;
  }

  /**
   * @notice Update admin fee
   * @dev Total fees cannot be greater than BIPS_DIVISOR (100%)
   * @param newValue specified in BIPS
   */
  function updateAdminFee(uint newValue) external onlyOwner {
    require(newValue.add(REINVEST_REWARD_BIPS) <= BIPS_DIVISOR, "admin fee too high");
    emit UpdateAdminFee(ADMIN_FEE_BIPS, newValue);
    ADMIN_FEE_BIPS = newValue;
  }

  /**
   * @notice Update reinvest reward
   * @dev Total fees cannot be greater than BIPS_DIVISOR (100%)
   * @param newValue specified in BIPS
   */
  function updateReinvestReward(uint newValue) external onlyOwner {
    require(newValue.add(ADMIN_FEE_BIPS) <= BIPS_DIVISOR, "reinvest reward too high");
    emit UpdateReinvestReward(REINVEST_REWARD_BIPS, newValue);
    REINVEST_REWARD_BIPS = newValue;
  }

  /**
   * @notice Recover ERC20 from contract
   * @param tokenAddress token address
   * @param tokenAmount amount to recover
   */
  function recoverERC20(address tokenAddress, uint tokenAmount) external onlyOwner {
    require(tokenAmount > 0, 'amount too low');
    IERC20(tokenAddress).transfer(msg.sender, tokenAmount);
    emit Recovered(tokenAddress, tokenAmount);
  }

  /**
   * @notice Recover AVAX from contract
   * @param amount amount
   */
  function recoverAVAX(uint amount) external onlyOwner {
    require(amount > 0, 'amount too low');
    msg.sender.transfer(amount);
    emit Recovered(address(0), amount);
  }
}

contracts/strategies/CompoundingBamboo.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;

import "../YakStrategy.sol";
import "../interfaces/IBambooChef.sol";
import "../interfaces/IBambooBar.sol";
import "../interfaces/IRouter.sol";
import "../interfaces/IPair.sol";

/**
 * @notice Single asset strategy for BAMBOO
 */
contract CompoundingBamboo is YakStrategy {
  using SafeMath for uint;

  IRouter public router;
  IBambooChef public stakingContract;
  IBambooBar public conversionContract;
  IERC20 public sBamboo;

  uint public PID;

  constructor(
    string memory _name,
    string memory _symbol,
    address _depositToken, 
    address _rewardToken, 
    address _stakingContract,
    address _conversionContract,
    address _timelock,
    uint _pid,
    uint _minTokensToReinvest,
    uint _adminFeeBips,
    uint _devFeeBips,
    uint _reinvestRewardBips
  ) {
    name = _name;
    symbol = _symbol;
    depositToken = IPair(_depositToken);
    rewardToken = IERC20(_rewardToken);
    stakingContract = IBambooChef(_stakingContract);
    conversionContract = IBambooBar(_conversionContract);
    sBamboo = IERC20(_conversionContract);
    PID = _pid;
    devAddr = 0xD15E8b816F040fB0d7495ebA36C16Cf0f33c049c;

    setAllowances();
    updateMinTokensToReinvest(_minTokensToReinvest);
    updateAdminFee(_adminFeeBips);
    updateDevFee(_devFeeBips);
    updateReinvestReward(_reinvestRewardBips);
    updateDepositsEnabled(true);
    transferOwnership(_timelock);

    emit Reinvest(0, 0);
  }

  /**
   * @notice Approve tokens for use in Strategy
   * @dev Restricted to avoid griefing attacks
   */
  function setAllowances() public override onlyOwner {
    depositToken.approve(address(conversionContract), MAX_UINT);
    sBamboo.approve(address(stakingContract), MAX_UINT);
  }

  /**
   * @notice Deposit tokens to receive receipt tokens
   * @param amount Amount of tokens to deposit
   */
  function deposit(uint amount) external override {
    _deposit(address(depositToken), msg.sender, amount);
  }

  /**
   * @notice Deposit using Permit
   * @param amount Amount of tokens to deposit
   * @param deadline The time at which to expire the signature
   * @param v The recovery byte of the signature
   * @param r Half of the ECDSA signature pair
   * @param s Half of the ECDSA signature pair
   */
  function depositWithPermit(uint amount, uint deadline, uint8 v, bytes32 r, bytes32 s) external override {
    depositToken.permit(msg.sender, address(this), amount, deadline, v, r, s);
    _deposit(address(depositToken), msg.sender, amount);
  }

  function depositFor(address account, uint amount) external override {
      _deposit(address(depositToken), account, amount);
  }

  function depositSBamboo(uint amount) external {
    _deposit(address(sBamboo), msg.sender, amount);
  }

  function depositSBambooWithPermit(uint amount, uint deadline, uint8 v, bytes32 r, bytes32 s) external {
    sBamboo.permit(msg.sender, address(this), amount, deadline, v, r, s);
    _deposit(address(sBamboo), msg.sender, amount);
  }

  function depositSBambooFor(address account, uint amount) external {
      _deposit(address(sBamboo), account, amount);
  }

  /**
   * @notice Deposit Bamboo or sBamboo
   * @param token address
   * @param account address
   * @param amount token amount
   */
  function _deposit(address token, address account, uint amount) internal {
    require(DEPOSITS_ENABLED == true, "CompoundingBamboo::_deposit");
    require(token == address(depositToken) || token == address(sBamboo), "CompoundingBamboo::_deposit, token not accepted");
    if (MAX_TOKENS_TO_DEPOSIT_WITHOUT_REINVEST > 0) {
        uint unclaimedRewards = checkReward();
        if (unclaimedRewards > MAX_TOKENS_TO_DEPOSIT_WITHOUT_REINVEST) {
            _reinvest(unclaimedRewards);
        }
    }

    uint depositTokenAmount;
    if (token == address(depositToken)) {
      require(depositToken.transferFrom(msg.sender, address(this), amount));
      depositTokenAmount = amount;
      _stakeDepositTokens(amount);
    }
    else if (token == address(sBamboo)) {
      require(sBamboo.transferFrom(msg.sender, address(this), amount));
      depositTokenAmount = _getBambooForSBamboo(amount);
      _stakeSBamboo(amount);
    }

    _mint(account, getSharesForDepositTokens(depositTokenAmount));
    totalDeposits = totalDeposits.add(depositTokenAmount);
    emit Deposit(account, depositTokenAmount);
  }

  function withdraw(uint amount) external override {
    uint depositTokenAmount = getDepositTokensForShares(amount);
    if (depositTokenAmount > 0) {
      _withdrawDepositTokens(depositTokenAmount);
      require(depositToken.transfer(msg.sender, depositTokenAmount), "CompoundingBamboo::withdraw");
      _burn(msg.sender, amount);
      totalDeposits = totalDeposits.sub(depositTokenAmount);
      emit Withdraw(msg.sender, depositTokenAmount);
    }
  }

  /**
   * @notice Withdraw Bamboo
   * @param amount deposit tokens
   */
  function _withdrawDepositTokens(uint amount) private {
    require(amount > 0, "CompoundingBamboo::_withdrawDepositTokens");
    uint sBambooAmount = _getSBambooForBamboo(amount);
    stakingContract.withdraw(PID, sBambooAmount);
    conversionContract.leave(sBambooAmount);
  }

  function reinvest() external override onlyEOA {
    uint unclaimedRewards = checkReward();
    require(unclaimedRewards >= MIN_TOKENS_TO_REINVEST, "CompoundingBamboo::reinvest");
    _reinvest(unclaimedRewards);
  }

  /**
    * @notice Reinvest rewards from staking contract to deposit tokens
    * @dev Reverts if the expected amount of tokens are not returned from `stakingContract`
    * @param amount deposit tokens to reinvest
    */
  function _reinvest(uint amount) private {
    stakingContract.deposit(PID, 0);

    uint devFee = amount.mul(DEV_FEE_BIPS).div(BIPS_DIVISOR);
    if (devFee > 0) {
      require(rewardToken.transfer(devAddr, devFee), "CompoundingBamboo::_reinvest, dev");
    }

    uint adminFee = amount.mul(ADMIN_FEE_BIPS).div(BIPS_DIVISOR);
    if (adminFee > 0) {
      require(rewardToken.transfer(owner(), adminFee), "CompoundingBamboo::_reinvest, admin");
    }

    uint reinvestFee = amount.mul(REINVEST_REWARD_BIPS).div(BIPS_DIVISOR);
    if (reinvestFee > 0) {
      require(rewardToken.transfer(msg.sender, reinvestFee), "CompoundingBamboo::_reinvest, reward");
    }

    uint depositTokenAmount = amount.sub(devFee).sub(adminFee).sub(reinvestFee);
    _stakeDepositTokens(depositTokenAmount);
    totalDeposits = totalDeposits.add(depositTokenAmount);

    emit Reinvest(totalDeposits, totalSupply);
  }

  /**
   * @notice Convert and stake Bamboo
   * @param amount deposit tokens
   */
  function _stakeDepositTokens(uint amount) private {
    uint sBambooAmount = _getSBambooForBamboo(amount);
    _convertBambooToSBamboo(amount);
    _stakeSBamboo(sBambooAmount);
  }

  /**
   * @notice Convert bamboo to sBamboo
   * @param amount deposit token
   */
  function _convertBambooToSBamboo(uint amount) private {
    require(amount > 0, "CompoundingBamboo::_convertBambooToSBamboo");
    conversionContract.enter(amount);
  }

  /**
   * @notice Stake sBamboo
   * @param amount sBamboo
   */
  function _stakeSBamboo(uint amount) private {
    require(amount > 0, "CompoundingBamboo::_stakeSBamboo");
    stakingContract.deposit(PID, amount);
  }

  function checkReward() public override view returns (uint) {
    uint pendingReward = stakingContract.pendingBamboo(PID, address(this));
    uint contractBalance = rewardToken.balanceOf(address(this));
    return pendingReward.add(contractBalance);
  }

  /**
   * @notice Estimate recoverable balance
   * @return deposit tokens
   */
  function estimateDeployedBalance() external override view returns (uint) {
    (uint depositBalance, ) = stakingContract.userInfo(PID, address(this));
    return _getBambooForSBamboo(depositBalance);
  }

  /**
   * @notice Conversion rate for Bamboo to sBamboo
   * @param amount Bamboo tokens
   * @return sBamboo shares
   */
  function _getSBambooForBamboo(uint amount) private view returns (uint) {
    uint bambooBalance = depositToken.balanceOf(address(conversionContract));
    uint sBambooShares = sBamboo.totalSupply();
    if (bambooBalance.mul(sBambooShares) == 0) {
      return amount;
    }
    return amount.mul(sBambooShares).div(bambooBalance);
  }

  /**
   * @notice Conversion rate for sBamboo to Bamboo
   * @param amount sBamboo shares
   * @return Bamboo tokens
   */
  function _getBambooForSBamboo(uint amount) private view returns (uint) {
    uint bambooBalance = depositToken.balanceOf(address(conversionContract));
    uint sBambooShares = sBamboo.totalSupply();
    if (bambooBalance.mul(sBambooShares) == 0) {
      return amount;
    }
    return amount.mul(bambooBalance).div(sBambooShares);
  }

  function rescueDeployedFunds(uint minReturnAmountAccepted, bool disableDeposits) external override onlyOwner {
    uint balanceBefore = depositToken.balanceOf(address(this));
    stakingContract.emergencyWithdraw(PID);
    conversionContract.leave(sBamboo.balanceOf(address(this)));
    uint balanceAfter = depositToken.balanceOf(address(this));
    require(balanceAfter.sub(balanceBefore) >= minReturnAmountAccepted, "CompoundingBamboo::rescueDeployedFunds");
    totalDeposits = balanceAfter;
    emit Reinvest(totalDeposits, totalSupply);
    if (DEPOSITS_ENABLED == true && disableDeposits == true) {
      updateDepositsEnabled(false);
    }
  }
}

